import React,{useState, useRef} from 'react';
import '../assets/css/list.css'
import recommendData from '../db/recommendList'
import followerListData from '../db/followerList'
import Follower from './../component/Follower';
import Recommend from './../component/Recommend';



export default function List() {
    const [idx, setIdx] = useState(0);  //메뉴
    const [like, unlike] = useState(false); // 좋아요
    const listView = useRef(); // 테스트
    var followerList;

    const menuArr =[
        {name:'추천', con:'recommendList',key:1},
        {name:'팔로잉', con:'followerList',key:2},
    ]
    const selectMenuHandler = (index) => {
        setIdx(index);
    };

    const likeClick = () =>{
        unlike(!like);
    }

    //추천게시글
    const recommendList = recommendData.recommendList.map((item) => {
        return (
          <li key={item.id} ref={listView}>
            <div className='tit-box'>
                <strong>{item.name}</strong>
                <div className='tit-btn'>
                    <button type='button'>팔로우</button>
                    <button type='button' className='tit-menu'></button>
                </div>
            </div>
            <div className='img-box'><img src={item.img} alt="" /></div>
            <div className='btn-box-wrap'>
                <div className='btn-box txt-btn'>
                    <button className='like'>{item.like} 개</button>
                    <button className='review'>{item.review} 개</button>
                </div>
                <div className='btn-box'>
                    <button type='button' className='share'><span className='hidden'>스크랩</span></button>
                    <button type='button' className='favorite'><span className='hidden'>즐겨찾기</span></button>
                </div>
            </div>
            <div className='txt-box'>
                <p>{item.txt}</p>
                <button type='button'>더보기</button>
            </div>
          </li>
        );
    });
    
    //팔로잉게시글
    const follower = followerListData.followerList.map((item) => {
        return (
            <li key={item.id}>
                <div className='img-box'><img src={item.img} alt="" /></div>
                <div className='txt-box'>
                    <strong>{item.tit}</strong>
                    <div>
                        <span>{item.name}</span>
                        <button type='button'>{item.like}</button>
                    </div>
                </div>
            </li>
        );
    });

    //팔로잉 게시글 없을때
    const followerNone = <li className="none-list">아직 팔로잉 한 사람이 없어요</li>;
    if(followerListData.length === 0){
        followerList = followerNone;
    }else{
        followerList = follower;
    }
    return (
        <section className='content'>
            <div className='inner main-list'>
                <div className='list-menu'>
                    <div className='list-btn round-btn'>
                        {menuArr.map((ele, index)=>{
                            return(
                                <button type="button" 
                                key={ele.key} 
                                className={idx === index ? 'on' :''}
                                onClick={()=> selectMenuHandler(index)}
                                >
                                    {ele.name}
                                </button>
                            )
                        })}
                    </div>
                    <div className='list-btn'>
                        <button type='button' className='on'>최신순</button>
                        <button type='button'>인기순</button>
                    </div>
                </div>
                {menuArr[idx].con === 'recommendList' ?   <Recommend list={recommendData.recommendList.like}/> : <Follower/>}
            </div>
        </section>
    );

    
}
